import React, { useState, useEffect } from 'react';
import { Search, MapPin, Sliders, Home, X, ChevronDown, Heart, Star } from 'lucide-react';

// Mock data for demonstration
const MOCK_PROPERTIES = [
  {
    id: 1,
    name: 'Modern Downtown Apartment',
    location: 'Aba, Abia State',
    price: 2500,
    bedrooms: 2,
    bathrooms: 2,
    area: 1200,
    rating: 4.8,
    reviews: 24,
    amenities: ['Air Conditioning', 'Washer', 'Dryer', 'Dishwasher', 'Parking'],
    imageUrl: 'data:image/jpeg;base64,',
    isFeatured: true,
  },
  {
    id: 2,
    name: 'Cozy Studio in Brooklyn',
    location: 'Onitsha, Anambra State',
    price: 1800,
    bedrooms: 1,
    bathrooms: 1,
    area: 650,
    rating: 4.5,
    reviews: 18,
    amenities: ['Air Conditioning', 'Heating', 'Pets Allowed'],
    imageUrl: 'data:image/jpeg;base64,',
    isFeatured: false,
  },
  {
    id: 3,
    name: 'Luxury Condo with City View',
    location: 'Awka, Asaba',
    price: 3200,
    bedrooms: 3,
    bathrooms: 2.5,
    area: 1800,
    rating: 4.9,
    reviews: 36,
    amenities: ['Pool', 'Gym', 'Parking', 'Security System', 'Balcony'],
    imageUrl: 'data:image/jpeg;base64,',
    isFeatured: true,
  },
  {
    id: 4,
    name: 'Charming Brownstone Apartment',
    location: 'Upper East Side, Imo State',
    price: 2800,
    bedrooms: 2,
    bathrooms: 1,
    area: 1000,
    rating: 4.6,
    reviews: 15,
    amenities: ['Furnished', 'Washer', 'Dryer', 'Heating'],
    imageUrl: 'data:image/jpeg;base64,',
    isFeatured: false,
  },
  {
    id: 5,
    name: 'Spacious Family Home',
    location: 'Queens, Lagos',
    price: 3500,
    bedrooms: 4,
    bathrooms: 3,
    area: 2200,
    rating: 4.7,
    reviews: 22,
    amenities: ['Yard', 'Garage', 'Dishwasher', 'Air Conditioning', 'Heating'],
    imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQI5k831XaDaVq_RnB6iH9MUwGr_hPxS7WK9w&s',
    isFeatured: false,
  },
  {
    id: 6,
    name: 'Waterfront Studio Apartment',
    location: 'Battery Park, Abuja',
    price: 2200,
    bedrooms: 1,
    bathrooms: 1,
    area: 750,
    rating: 4.4,
    reviews: 12,
    amenities: ['Balcony', 'Security System', 'Air Conditioning', 'Heating'],
    imageUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQcLf3foXvRQFA6g1bWLzzPfJ5C_-v8lolTYg&s',
    isFeatured: true,
  },
];

// Location options for filter
const LOCATIONS = ['All Locations', 'Downtown, New York', 'Brooklyn, New York', 'Midtown, New York', 'Queens, New York', 'Upper East Side, New York', 'Battery Park, New York'];

// Amenities for filter
const AMENITIES = ['Air Conditioning', 'Heating', 'Washer', 'Dryer', 'Dishwasher', 'Refrigerator', 'Microwave', 'Balcony', 'Pool', 'Gym', 'Parking', 'Garage', 'Pets Allowed', 'Furnished', 'Security System'];

const HomePage = () => {
  // State for filters
  const [filters, setFilters] = useState({
    location: 'All Locations',
    minPrice: '',
    maxPrice: '',
    bedrooms: '',
    bathrooms: '',
    amenities: [],
  });
  
  const [showFilters, setShowFilters] = useState(false);
  const [filteredProperties, setFilteredProperties] = useState(MOCK_PROPERTIES);
  const [searchQuery, setSearchQuery] = useState('');
  const [favorites, setFavorites] = useState([]);
  
  // Filter properties based on current filters
  useEffect(() => {
    let results = MOCK_PROPERTIES;
    
    // Filter by search query
    if (searchQuery) {
      const query = searchQuery.toLowerCase();
      results = results.filter(property => 
        property.name.toLowerCase().includes(query) || 
        property.location.toLowerCase().includes(query)
      );
    }
    
    // Filter by location
    if (filters.location !== 'All Locations') {
      results = results.filter(property => property.location === filters.location);
    }
    
    // Filter by price range
    if (filters.minPrice) {
      results = results.filter(property => property.price >= parseInt(filters.minPrice));
    }
    if (filters.maxPrice) {
      results = results.filter(property => property.price <= parseInt(filters.maxPrice));
    }
    
    // Filter by bedrooms
    if (filters.bedrooms) {
      results = results.filter(property => property.bedrooms >= parseInt(filters.bedrooms));
    }
    
    // Filter by bathrooms
    if (filters.bathrooms) {
      results = results.filter(property => property.bathrooms >= parseFloat(filters.bathrooms));
    }
    
    // Filter by amenities
    if (filters.amenities.length > 0) {
      results = results.filter(property => 
        filters.amenities.every(amenity => property.amenities.includes(amenity))
      );
    }
    
    setFilteredProperties(results);
  }, [filters, searchQuery]);
  
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFilters({
      ...filters,
      [name]: value
    });
  };
  
  const handleAmenityToggle = (amenity) => {
    const updatedAmenities = filters.amenities.includes(amenity)
      ? filters.amenities.filter(a => a !== amenity)
      : [...filters.amenities, amenity];
    
    setFilters({
      ...filters,
      amenities: updatedAmenities
    });
  };
  
  const clearFilters = () => {
    setFilters({
      location: 'All Locations',
      minPrice: '',
      maxPrice: '',
      bedrooms: '',
      bathrooms: '',
      amenities: [],
    });
    setSearchQuery('');
  };
  
  const toggleFavorite = (propertyId) => {
    if (favorites.includes(propertyId)) {
      setFavorites(favorites.filter(id => id !== propertyId));
    } else {
      setFavorites([...favorites, propertyId]);
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <div className="relative bg-blue-800 text-white">
        <div className="absolute inset-0 overflow-hidden">
          {/* Background image placeholder */}
          <div className="absolute inset-0 bg-gradient-to-br from-blue-900 to-blue-600 opacity-90"></div>
        </div>
        
        <div className="relative max-w-7xl mx-auto px-4 py-24 sm:px-6 lg:px-8">
          <h1 className="text-4xl font-extrabold tracking-tight sm:text-5xl lg:text-6xl">
            Find Your Perfect Rental Home
          </h1>
          <p className="mt-6 text-xl max-w-3xl">
            Discover thousands of rental properties in your area. From cozy apartments to spacious houses, find the perfect place to call home.
          </p>
          
          {/* Search Box */}
          <div className="mt-10">
            <div className="max-w-xl mx-auto bg-white rounded-full shadow-md flex items-center p-1">
              <div className="flex-grow">
                <input
                  type="text"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  placeholder="Search by location or property name..."
                  className="w-full bg-transparent border-0 focus:ring-0 text-gray-900 px-4 py-3"
                />
              </div>
              <button className="bg-blue-600 text-white p-3 rounded-full hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500">
                <Search size={20} />
              </button>
            </div>
          </div>
        </div>
      </div>
      
      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 py-12 sm:px-6 lg:px-8">
        {/* Filter Controls */}
        <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-8 space-y-4 md:space-y-0">
          <div className="flex items-center">
            <h2 className="text-2xl font-bold text-gray-900">
              Available Properties
              {filteredProperties.length > 0 && (
                <span className="ml-2 text-gray-500 text-base font-normal">
                  ({filteredProperties.length} {filteredProperties.length === 1 ? 'property' : 'properties'})
                </span>
              )}
            </h2>
          </div>
          
          <div className="flex items-center space-x-4">
            <button
              onClick={() => setShowFilters(!showFilters)}
              className="inline-flex items-center px-4 py-2 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <Sliders className="h-4 w-4 mr-2" />
              Filters
            </button>
            
            <div className="relative inline-block text-left">
              <select
                className="block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm rounded-md"
              >
                <option>Sort: Featured</option>
                <option>Price: Low to High</option>
                <option>Price: High to Low</option>
                <option>Newest First</option>
                <option>Best Rated</option>
              </select>
            </div>
          </div>
        </div>
        
        {/* Filter Panel */}
        {showFilters && (
          <div className="bg-white rounded-lg shadow mb-8 p-6">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-medium text-gray-900">Filters</h3>
              <button
                onClick={clearFilters}
                className="text-sm text-blue-600 hover:text-blue-800"
              >
                Clear all filters
              </button>
            </div>
            
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              {/* Location Filter */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Location
                </label>
                <select
                  name="location"
                  value={filters.location}
                  onChange={handleInputChange}
                  className="block w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                >
                  {LOCATIONS.map((location, index) => (
                    <option key={index} value={location}>{location}</option>
                  ))}
                </select>
              </div>
              
              {/* Price Range */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Price Range
                </label>
                <div className="flex space-x-2">
                  <input
                    type="number"
                    name="minPrice"
                    value={filters.minPrice}
                    onChange={handleInputChange}
                    placeholder="Min $"
                    className="block w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                  />
                  <input
                    type="number"
                    name="maxPrice"
                    value={filters.maxPrice}
                    onChange={handleInputChange}
                    placeholder="Max $"
                    className="block w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                  />
                </div>
              </div>
              
              {/* Bedrooms & Bathrooms */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Bedrooms
                </label>
                <select
                  name="bedrooms"
                  value={filters.bedrooms}
                  onChange={handleInputChange}
                  className="block w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                >
                  <option value="">Any</option>
                  <option value="1">1+</option>
                  <option value="2">2+</option>
                  <option value="3">3+</option>
                  <option value="4">4+</option>
                  <option value="5">5+</option>
                </select>
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Bathrooms
                </label>
                <select
                  name="bathrooms"
                  value={filters.bathrooms}
                  onChange={handleInputChange}
                  className="block w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                >
                  <option value="">Any</option>
                  <option value="1">1+</option>
                  <option value="1.5">1.5+</option>
                  <option value="2">2+</option>
                  <option value="2.5">2.5+</option>
                  <option value="3">3+</option>
                </select>
              </div>
            </div>
            
            {/* Amenities */}
            <div className="mt-6">
              <h4 className="text-sm font-medium text-gray-700 mb-3">Amenities</h4>
              <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-3">
                {AMENITIES.map((amenity) => (
                  <div key={amenity} className="flex items-center">
                    <input
                      type="checkbox"
                      id={`amenity-${amenity}`}
                      checked={filters.amenities.includes(amenity)}
                      onChange={() => handleAmenityToggle(amenity)}
                      className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                    />
                    <label htmlFor={`amenity-${amenity}`} className="ml-2 text-sm text-gray-700">
                      {amenity}
                    </label>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}
        
        {/* Property Cards */}
        {filteredProperties.length === 0 ? (
          <div className="text-center py-12">
            <Home className="mx-auto h-12 w-12 text-gray-400" />
            <h3 className="mt-2 text-xl font-medium text-gray-900">No properties found</h3>
            <p className="mt-1 text-gray-500">Try adjusting your search or filters to find what you're looking for.</p>
            <button 
              onClick={clearFilters}
              className="mt-4 inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              Clear all filters
            </button>
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
            {filteredProperties.map((property) => (
              <div key={property.id} className="bg-white rounded-lg shadow-md overflow-hidden">
                {/* Property Image */}
                <div className="relative">
                  <img 
                    src={property.imageUrl} 
                    alt={property.name} 
                    className="h-48 w-full object-cover"
                  />
                  <button 
                    onClick={() => toggleFavorite(property.id)}
                    className="absolute top-3 right-3 p-1.5 bg-white rounded-full shadow-md hover:bg-gray-100 focus:outline-none"
                  >
                    <Heart 
                      className={`h-5 w-5 ${favorites.includes(property.id) ? 'text-red-500 fill-red-500' : 'text-gray-400'}`} 
                    />
                  </button>
                  {property.isFeatured && (
                    <div className="absolute top-3 left-3 bg-blue-600 text-white text-xs font-semibold px-2 py-1 rounded">
                      Featured
                    </div>
                  )}
                </div>
                
                {/* Property Details */}
                <div className="p-4">
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="text-lg font-semibold text-gray-900 line-clamp-1">{property.name}</h3>
                      <div className="flex items-center mt-1">
                        <MapPin className="h-4 w-4 text-gray-500 mr-1 flex-shrink-0" />
                        <p className="text-sm text-gray-500 line-clamp-1">{property.location}</p>
                      </div>
                    </div>
                    <p className="text-lg font-bold text-blue-600">N{property.price}/mo</p>
                  </div>
                  
                  {/* Property Specs */}
                  <div className="flex items-center justify-between mt-4 text-sm text-gray-500">
                    <div>{property.bedrooms} {property.bedrooms === 1 ? 'Bed' : 'Beds'}</div>
                    <div>{property.bathrooms} {property.bathrooms === 1 ? 'Bath' : 'Baths'}</div>
                    <div>{property.area} sq.ft</div>
                  </div>
                  
                  {/* Amenities Preview */}
                  <div className="mt-4">
                    <div className="flex flex-wrap gap-2">
                      {property.amenities.slice(0, 3).map((amenity, index) => (
                        <span key={index} className="bg-gray-100 text-gray-800 text-xs font-medium px-2 py-1 rounded">
                          {amenity}
                        </span>
                      ))}
                      {property.amenities.length > 3 && (
                        <span className="bg-gray-100 text-gray-800 text-xs font-medium px-2 py-1 rounded">
                          +{property.amenities.length - 3} more
                        </span>
                      )}
                    </div>
                  </div>
                  
                  <div className="flex items-center justify-between mt-4">
                    <div className="flex items-center">
                      <Star className="h-4 w-4 text-yellow-400" />
                      <span className="text-sm font-medium text-gray-900 ml-1">{property.rating}</span>
                      <span className="text-sm text-gray-500 ml-1">({property.reviews} reviews)</span>
                    </div>
                    <button className="text-blue-600 text-sm font-medium hover:text-blue-800">
                      View Details
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
        
        {/* Pagination */}
        <div className="mt-12 flex items-center justify-center">
          <nav className="inline-flex rounded-md shadow">
            <button className="px-3 py-2 rounded-l-md border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50">
              Previous
            </button>
            <button className="px-3 py-2 border-t border-b border-gray-300 bg-white text-sm font-medium text-blue-600">
              1
            </button>
            <button className="px-3 py-2 border-t border-b border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50">
              2
            </button>
            <button className="px-3 py-2 border-t border-b border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50">
              3
            </button>
            <button className="px-3 py-2 border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50 rounded-r-md">
              Next
            </button>
          </nav>
        </div>
      </div>
    </div>
  );
};

export default HomePage;